mapper.update:
UPDATE sys_user
nickname = #{nickname}, email = #{email}, phone = #{phone}, status = #{status}, avatar = #{avatar}, password = #{password}, remark = #{remark}, pwd_updated_at = #{pwdUpdatedAt},
WHERE id = #{id}

mapper.select:
SELECT COUNT(1) FROM sys_user
AND (username LIKE CONCAT('%', #{keyword}, '%') OR nickname LIKE CONCAT('%', #{keyword}, '%') OR email LIKE CONCAT('%', #{keyword}, '%') OR phone LIKE CONCAT('%', #{keyword}, '%'))
AND status = #{status}
